              <img src={answer4 != 1 ? require("./images/orange.png") : require("./images/orange2.png")} alt="" />
              <img src={require("./images/orange1.png")} alt="" />
              <img className={doorType4 == 0 ? "menL" : "hide"} src={require("./images/men.png")} alt="" />
