              <img src={require("./images/pink1.png")} alt="" />
              <img className={doorType5 == 0 ? "menL" : "hide"} src={require("./images/men.png")} alt="" />
              <img className={doorType5 == 0 ? "menR" : "hide"} src={require("./images/men.png")} alt="" />
            </div>
